import React from 'react';

const ExperienceSection = ({ experiences }) => {
  return (
    <div className="py-12 bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center">
          <h2 className="text-3xl font-extrabold text-gray-900 sm:text-4xl">
            工作经历
          </h2>
        </div>

        <div className="mt-10 space-y-10">
          {experiences.map((exp) => (
            <div key={exp.id} className="bg-white rounded-lg shadow p-6">
              <div className="flex flex-col sm:flex-row sm:justify-between sm:items-start">
                <div>
                  <h3 className="text-xl font-bold text-gray-900">{exp.position}</h3>
                  <p className="text-lg text-gray-600">{exp.company}</p>
                </div>
                <div className="mt-2 sm:mt-0">
                  <p className="text-gray-500">
                    {exp.start_date} - {exp.end_date || '至今'}
                  </p>
                </div>
              </div>
              <div className="mt-4">
                <p className="text-gray-600 whitespace-pre-line">{exp.description}</p>
              </div>
              {exp.achievements && exp.achievements.length > 0 && (
                <div className="mt-4">
                  <h4 className="font-medium text-gray-900">主要成就:</h4>
                  <ul className="mt-2 space-y-1">
                    {exp.achievements.map((achievement, index) => (
                      <li key={index} className="flex items-start">
                        <div className="flex-shrink-0 h-5 w-5 text-blue-500">•</div>
                        <p className="ml-2 text-gray-600">{achievement}</p>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ExperienceSection;
