import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDatabase, faTools, faPlusCircle, faTrashAlt, faArrowUp, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import Slider from 'react-slider';

const SkillsEditor = ({ data, onSave }) => {
  const [formData, setFormData] = useState({
    databases: [],
    tools: []
  });
  const [isSaving, setIsSaving] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);

  // 初始化表单数据
  useEffect(() => {
    if (data) {
      setFormData({
        databases: data.databases || [],
        tools: data.tools || []
      });
    }
  }, [data]);

  // 添加新技能
  const addSkill = (category) => {
    const newSkill = { name: '', level: 50 };
  setFormData(prev => ({
      ...prev,
      [category]: [...prev[category], newSkill]
    }));
    setSaveStatus(null);
  };

  // 删除技能
  const removeSkill = (category, index) => {
    setFormData(prev => ({
      ...prev,
      [category]: prev[category].filter((_, i) => i !== index)
    }));
    setSaveStatus(null);
  };

  // 更新技能名称
  const updateSkillName = (category, index, name) => {
    const updatedSkills = [...formData[category]];
    updatedSkills[index].name = name;
    setFormData(prev => ({
      ...prev,
      [category]: updatedSkills
    }));
    setSaveStatus(null);
  };

  // 更新技能熟练度
  const updateSkillLevel = (category, index, level) => {
    const updatedSkills = [...formData[category]];
    updatedSkills[index].level = level;
    setFormData(prev => ({
      ...prev,
      [category]: updatedSkills
    }));
    setSaveStatus(null);
  };

  // 调整技能顺序
  const reorderSkill = (category, fromIndex, toIndex) => {
    const updatedSkills = [...formData[category]];
    const [removed] = updatedSkills.splice(fromIndex, 1);
    updatedSkills.splice(toIndex, 0, removed);
    setFormData(prev => ({
      ...prev,
      [category]: updatedSkills
    }));
    setSaveStatus(null);
  };

  // 保存技能数据
  const handleSave = async () => {
    // 验证所有技能都有名称
    const hasEmptyName = [...formData.databases, ...formData.tools].some(skill => !skill.name.trim());
    if (hasEmptyName) {
      setSaveStatus('error');
      setTimeout(() => setSaveStatus(null), 3000);
      return;
    }

    setIsSaving(true);
    const success = await onSave(formData);
    setSaveStatus(success ? 'success' : 'error');
    setIsSaving(false);

    if (success) {
      setTimeout(() => setSaveStatus(null), 3000);
    }
  };

  // 渲染技能列表
  const renderSkillList = (category, icon, title) => {
    return (
    <div className="mb-8">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-700 flex items-center">
          <FontAwesomeIcon icon={icon} className="mr-2 text-primary" /> {title}
        </h3>
        <button
          type="button"
          onClick={() => addSkill(category)}
          className="text-primary hover:text-primary/80"
        >
          <FontAwesomeIcon icon={faPlusCircle} className="mr-1" /> 添加技能
        </button>
      </div>

      {formData[category].length === 0 ? (
        <div className="text-center py-8 border border-dashed border-gray-300 rounded-md text-gray-500">
          点击上方"添加技能"按钮开始添加{title.toLowerCase()}
        </div>
      ) : (
        <div className="space-y-6">
          {formData[category].map((skill, index) => (
            <div key={index} className="bg-gray-50 p-4 rounded-lg border border-gray-200">
              <div className="flex justify-between items-start mb-3">
                <div className="flex items-center space-x-2">
                  <button
                    type="button"
                    onClick={() => reorderSkill(category, index, index - 1)}
                    disabled={index === 0}
                    className={`p-1 rounded ${index === 0 ? 'text-gray-300' : 'text-gray-600 hover:text-primary'}`}
                  >
                    <FontAwesomeIcon icon={faArrowUp} size="sm" />
                  </button>
                  <button
                    type="button"
                    onClick={() => reorderSkill(category, index, index + 1)}
                    disabled={index === formData[category].length - 1}
                    className={`p-1 rounded ${index === formData[category].length - 1 ? 'text-gray-300' : 'text-gray-600 hover:text-primary'}`}
                  >
                    <FontAwesomeIcon icon={faArrowDown} size="sm" />
                  </button>
                  <input
                    type="text"
                    value={skill.name}
                    onChange={(e) => updateSkillName(category, index, e.target.value)}
                    className="flex-grow px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-primary focus:border-primary"
                    placeholder="技能名称"
                  />
                  <button
                    type="button"
                    onClick={() => removeSkill(category, index)}
                    className="text-red-500 hover:text-red-700 p-2"
                  >
                    <FontAwesomeIcon icon={faTrashAlt} size="sm" />
                  </button>
                </div>
                <span className="text-sm font-medium text-gray-600 w-12 text-right">
                  {Math.round(skill.level / 10)}
                </span>
              </div>
              <div className="px-2">
                <div className="mb-2 text-sm text-gray-600">技能水平 (1-10)</div>
<Slider
                  value={skill.level}
                  onChange={(value) => updateSkillLevel(category, index, value)}
                  min={0}
                      max={100}
                      step={10}
                      className="my-2"
                      trackClassName="bg-gray-200 h-2 rounded-full cursor-pointer"
                      thumbClassName="bg-primary w-5 h-5 rounded-full border-2 border-white shadow-sm -mt-1.5 relative after:content-[''] after:absolute after:bottom-[-10px] after:left-1/2 after:transform after:-translate-x-1/2 after:w-0.5 after:h-5 after:bg-primary"
                      renderTrack={(props, state) => (
                        <div
                          {...props}
                          className="relative"
                        >
                          <div
                            className="absolute h-full bg-primary rounded-full"
                            style={{ width: `${state.valueNow}%` }}
                          ></div>
                        </div>
                      )}
                    />
                    <div className="flex justify-between items-center my-2 px-1 w-full">
      {[...Array(10)].map((_, i) => {
        const currentLevel = skill.level - 1;
        return (
          <React.Fragment key={i}>
            <span className={`w-0.5 ${i === currentLevel ? 'h-4' : 'h-2'} bg-primary transition-all duration-200`}></span>
            {i < 9 && <span className="flex-1 h-0.5 bg-gray-200"></span>}
          </React.Fragment>
        );
      })}
    </div>
    <div className="flex justify-between text-xs text-gray-500 mt-1">
      {[...Array(10)].map((_, i) => (
        <span key={i}>{i + 1}</span>
      ))}
    </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
    );
  }


  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-2xl font-bold text-gray-900 mb-4">技能特长编辑</h2>
        <p className="text-gray-600 mb-6">添加您掌握的数据库技术和工具技能，并设置熟练度（1-10整数等级）</p>
      </div>

      <>
        {renderSkillList('databases', faDatabase, '数据库技术')}
        {renderSkillList('tools', faTools, '工具与技术')}
      </>

      <div className="flex justify-end items-center space-x-4 pt-4 border-t border-gray-200">
        <button
          type="button"
          onClick={handleSave}
          disabled={isSaving}
          className={`px-6 py-2 rounded-md font-medium transition-colors ${isSaving
            ? 'bg-gray-400 text-white cursor-not-allowed'
            : 'bg-primary text-white hover:bg-primary/90'}
          `}
        >
          {isSaving ? '保存中...' : '保存技能信息'}
        </button>
        {saveStatus === 'success' && (
          <div className="text-green-600 flex items-center">
            <i className="fa fa-check-circle mr-1"></i> 保存成功
          </div>
        )}
        {saveStatus === 'error' && (
          <div className="text-red-600 flex items-center">
            <i className="fa fa-exclamation-circle mr-1"></i> 请确保所有技能都有名称
          </div>
        )}
      </div>
    </div>
  );
};


export default SkillsEditor;
