import React from 'react';

const Hero = ({ profile }) => {
  if (!profile) return null;

  return (
    <div className="bg-gradient-to-r from-blue-50 to-indigo-50 py-16">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-gray-900 sm:text-5xl mb-4">
            {profile.name}
          </h1>
          <p className="text-xl text-indigo-600 font-medium mb-6">
            {profile.title}
          </p>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto mb-8">
            {profile.bio}
          </p>
          <div className="flex flex-col sm:flex-row items-start space-y-4 sm:space-y-0 sm:space-x-8">
            {profile.email && (
              <a href={`mailto:${profile.email}`} className="flex items-center text-gray-600 hover:text-indigo-700 transition-colors duration-300">
                <svg className="h-4 w-4 mr-2" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true">
                  <path d="M1.5 7.5v9c0 .83.67 1.5 1.5 1.5h18c.83 0 1.5-.67 1.5-1.5v-9c0-.83-.67-1.5-1.5-1.5H3c-.83 0-1.5.67-1.5 1.5z" />
                  <path d="M22.5 7.5l-9.5 6.5c-.3.2-.7.2-1 0L2.5 7.5" />
                </svg>
                <span>{profile.email}</span>
              </a>
            )}
            {profile.phone && (
              <a href={`tel:${profile.phone}`} className="flex items-center text-gray-600 hover:text-indigo-700 transition-colors duration-300">
                <svg className="h-4 w-4 mr-2" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true">
                  <path d="M6.62 10.79c1.44 2.83 3.76 5.14 6.59 6.59l2.2-2.2c.27-.27.67-.36 1.02-.24 1.12.37 2.33.57 3.57.57.55 0 1 .45 1 1V20c0 .55-.45 1-1 1-9.39 0-17-7.61-17-17 0-.55.45-1 1-1h3.5c.55 0 1 .45 1 1 0 1.25.2 2.45.57 3.57.11.35.03.74-.25 1.02l-2.2 2.2z" />
                </svg>
                <span>{profile.phone}</span>
              </a>
            )}
            {profile.location && (
              <div className="flex items-center text-gray-600">
                <svg className="h-4 w-4 mr-2" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true">
                  <path d="M12 2C8.13 2 5 5.13 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-3.87-3.13-7-7-7zm0 9.5c-1.38 0-2.5-1.12-2.5-2.5s1.12-2.5 2.5-2.5 2.5 1.12 2.5 2.5-1.12 2.5-2.5 2.5z" />
                </svg>
                <span>{profile.location}</span>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Hero;
