import React, { useState, useEffect } from 'react';
import Layout from '../layout';
import Hero from '../components/resume/hero';
import Skills from '../components/resume/skills';
import ExperienceSection from '../components/resume/experience';
import Projects from '../components/resume/projects';
import EducationSection from '../components/resume/education';
import Footer from '../components/resume/footer';
import { Profile, Experience, Project, Skill, Education } from '../entities/all';

export default function Resume() {
  const [profile, setProfile] = useState(null);
  const [experiences, setExperiences] = useState([]);
  const [projects, setProjects] = useState([]);
  const [skills, setSkills] = useState([]);
  const [education, setEducation] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);

        // Load all data in parallel
        const [profileData, experienceData, projectData, skillData, educationData] = await Promise.all([
          Profile.list('-created_date'),
          Experience.list('-start_date'),
          Project.list('-start_date'),
          Skill.list('category'),
          Education.list('-start_date')
        ]);

        setProfile(profileData[0] || null);
        setExperiences(experienceData || []);
        setProjects(projectData || []);
        setSkills(skillData || []);
        setEducation(educationData || []);

        setLoading(false);
      } catch (err) {
        console.error('加载数据失败:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    loadData();
  }, []);

  if (loading) {
    return (
      <Layout>
        <div className="flex justify-center items-center h-64">
          <div className="text-lg text-gray-600">加载中...</div>
        </div>
      </Layout>
    );
  }

  if (error) {
    return (
      <Layout>
        <div className="flex justify-center items-center h-64">
          <div className="text-lg text-red-600">加载失败: {error}</div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="space-y-16">
        {profile && <Hero profile={profile} />}
        {skills.length > 0 && <Skills skills={skills} />}
        {experiences.length > 0 && <ExperienceSection experiences={experiences} />}
        {projects.length > 0 && <Projects projects={projects} />}
        {education.length > 0 && <EducationSection education={education} />}
        <Footer />
      </div>
    </Layout>
  );
}
