import React from 'react';

const EducationSection = ({ education }) => {
  return (
    <div className="py-12 bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center">
          <h2 className="text-3xl font-extrabold text-gray-900 sm:text-4xl">
            教育背景
          </h2>
        </div>

        <div className="mt-10 space-y-8">
          {education.map((edu) => (
            <div key={edu.id} className="bg-white rounded-lg shadow p-6">
              <div className="flex flex-col sm:flex-row sm:justify-between sm:items-start">
                <div>
                  <h3 className="text-xl font-bold text-gray-900">{edu.school}</h3>
                  <p className="text-lg text-gray-600">{edu.degree} in {edu.major}</p>
                  {edu.gpa && (
                    <p className="text-gray-500 mt-1">GPA: {edu.gpa}</p>
                  )}
                </div>
                <div className="mt-2 sm:mt-0">
                  <p className="text-gray-500">
                    {edu.start_date} - {edu.end_date}
                  </p>
                </div>
              </div>
              {edu.description && (
                <div className="mt-4">
                  <p className="text-gray-600 whitespace-pre-line">{edu.description}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default EducationSection;
