// 简化版技能数据模型

// 模拟数据存储
let skills = [
  {
    id: '1',
    name: 'React',
    category: 'frontend',
    level: 9,
    years_experience: 4,
    description: '熟练掌握React生态系统，包括Hooks、Context、Redux等'
  },
  {
    id: '2',
    name: 'Vue.js',
    category: 'frontend',
    level: 8,
    years_experience: 3,
    description: '熟悉Vue.js及其生态系统，包括Vuex、Vue Router等'
  },
  {
    id: '3',
    name: 'JavaScript',
    category: 'frontend',
    level: 9,
    years_experience: 5,
    description: '深入理解JavaScript核心概念，包括ES6+特性'
  },
  {
    id: '4',
    name: 'HTML/CSS',
    category: 'frontend',
    level: 9,
    years_experience: 5,
    description: '精通HTML5和CSS3，熟悉响应式设计和移动端适配'
  },
  {
    id: '5',
    name: 'Node.js',
    category: 'backend',
    level: 7,
    years_experience: 2,
    description: '熟悉Node.js开发，有Express和Koa框架使用经验'
  },
  {
    id: '6',
    name: 'MongoDB',
    category: 'database',
    level: 7,
    years_experience: 2,
    description: '熟悉MongoDB数据库设计和优化'
  }
];

class Skill {
  // 获取技能列表
  static async list(orderBy = 'category') {
    // 模拟异步操作
    return new Promise(resolve => {
      setTimeout(() => {
        // 按类别排序
        if (orderBy === 'category') {
          resolve([...skills].sort((a, b) => a.category.localeCompare(b.category)));
        } else {
          resolve(skills);
        }
      }, 100);
    });
  }

  // 创建新技能
  static async create(data) {
    return new Promise(resolve => {
      setTimeout(() => {
        const newSkill = {
          id: String(skills.length + 1),
          ...data
        };
        skills.push(newSkill);
        resolve(newSkill);
      }, 100);
    });
  }

  // 更新技能
  static async update(id, data) {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        const index = skills.findIndex(s => s.id === id);
        if (index === -1) {
          reject(new Error('技能未找到'));
          return;
        }

        skills[index] = { ...skills[index], ...data };
        resolve(skills[index]);
      }, 100);
    });
  }
}

export default Skill;
