// 简化版项目数据模型

// 模拟数据存储
let projects = [
  {
    id: '1',
    name: '电商平台前端重构',
    description: '负责公司核心电商平台的前端重构工作，使用React技术栈构建高性能的用户界面。',
    technologies: ['React', 'Redux', 'Webpack', 'Sass'],
    link: 'https://example-ecommerce.com',
    github: 'https://github.com/zhangsan/ecommerce',
    image_url: '',
    featured: true,
    start_date: '2023-01-01',
    end_date: '2023-06-30',
    team_size: 5,
    role: '前端技术负责人',
    achievements: [
      '将页面加载速度提升了50%',
      '实现了组件库的标准化，提高了开发效率',
      '优化了移动端用户体验'
    ]
  },
  {
    id: '2',
    name: '企业管理系统',
    description: '开发企业内部管理系统，包括员工管理、考勤统计、报表生成等功能模块。',
    technologies: ['Vue.js', 'Vuex', 'Element UI', 'Node.js'],
    link: '',
    github: 'https://github.com/zhangsan/erp-system',
    image_url: '',
    featured: false,
    start_date: '2022-03-01',
    end_date: '2022-09-30',
    team_size: 3,
    role: '全栈开发工程师',
    achievements: [
      '独立完成了前端和后端的开发工作',
      '实现了数据可视化功能，方便管理层决策',
      '系统上线后提高了办公效率30%'
    ]
  }
];

class Project {
  // 获取项目列表
  static async list(orderBy = '-start_date') {
    // 模拟异步操作
    return new Promise(resolve => {
      setTimeout(() => {
        // 简单排序实现
        if (orderBy === '-start_date') {
          resolve([...projects].sort((a, b) => new Date(b.start_date) - new Date(a.start_date)));
        } else {
          resolve(projects);
        }
      }, 100);
    });
  }

  // 创建新项目
  static async create(data) {
    return new Promise(resolve => {
      setTimeout(() => {
        const newProject = {
          id: String(projects.length + 1),
          ...data
        };
        projects.push(newProject);
        resolve(newProject);
      }, 100);
    });
  }

  // 更新项目
  static async update(id, data) {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        const index = projects.findIndex(p => p.id === id);
        if (index === -1) {
          reject(new Error('项目未找到'));
          return;
        }

        projects[index] = { ...projects[index], ...data };
        resolve(projects[index]);
      }, 100);
    });
  }
}

export default Project;
